import React from 'react';
import { View, Text, Image, StyleSheet } from 'react-native';
import icone from '../assets/icon-TCC.png';
import { TouchableOpacity } from 'react-native-gesture-handler';

const DegradedButton = ({ text, onPress }) => {
  return (
    <TouchableOpacity
      style={{
        background: 'linear-gradient(to right, #924DC1, #FFA500)',
        borderRadius: 20,
        height: 40,
        width: 120,
        justifyContent: 'center',
        alignItems: 'center',
      }}
      onPress={onPress}
    >
      <Text style={{ color: 'white', fontWeight: '500' }}>{text}</Text>
    </TouchableOpacity>
  );
};

const MinhaPagina = () => {
  return (
    <View style={styles.container}>
      <View style={styles.logoContainer}></View>

      <View style={styles.circuloLine1} />
      <View style={styles.circulo} />

      <View style={styles.circuloLine2} />
      <View style={styles.circuloTop} />

      <Image source={icone} style={styles.logo} />

      <Text style={styles.titulo}>SpaceSync</Text>

      <DegradedButton text="START" onPress={() => alert('Botão START Clicado!')} />
    </View>
  );
};

const styles = StyleSheet.create({
  // ... Seus estilos existentes ...
});

export default MinhaPagina;
