import React from 'react';
import { View, Text, StyleSheet } from 'react-native';

const Ver = () => {
  return (
    <View style={styles.container}>
      <Text style={styles.text}>AMBIENTES DISPON√çVEIS</Text>

      <View style={styles.labContainer}>
        <Text style={styles.lab1}>LAB 1</Text>
        <Text style={styles.lab2}>LAB 2</Text>
      </View>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  text: {
    position: 'relative',
    fontWeight: 'bold',
    fontSize: 25,
    bottom: "30%",
  },
  labContainer: {
    justifyContent: 'space-between',
    flexDirection: "column",
    backgroundColor: '#E1CBEE',
    padding: 7,
    alignItems: 'center',
  },
  lab1: {
    width: '100%',
    marginVertical: 70, // Adiciona margem vertical para separar os textos
  },
});

export default Ver;
