import React, { useState } from 'react';
import { View, TextInput, Button, StyleSheet, KeyboardAvoidingView, Platform, Image, Text, TouchableOpacity } from 'react-native';

import api from './services/api';

import icone from '../assets/icon-TCC.png';

export default function CadastroScreen({ navigation }) {
  const [nome, setNome] = useState('');
  const [email, setEmail] = useState('');
  const [senha, setSenha] = useState('');

  const handleCadastro = () => {
    // Implemente a lógica de cadastro aqui

    console.log('Nome:', nome);
    console.log('Email:', email);
    console.log('Senha:', senha);
  };

  async function verLogin() {
    let logou = false;
    let nome = '';
    let id = '';
    let info = {};

    try {
      let dadosUsu = {
        usu_email: email,
        usu_senha: senha
      };

      const response = await api.post('login', dadosUsu);
      logou = response.data.confirma; 
      nome = response.data.message[0].usu_nome; 
      id = response.data.message[0].usu_id; 
      info = { id, nome } 
      
      // console.log(response.data.nome);
    } catch (err) {
      console.log('Erro: ' + err);
    }
    console.log(info);
    if (logou) {
      navigation.navigate('welcome', {info});
    } else {
      alert('E-mail ou senha inválidos');
    }

  }

  return (
    <KeyboardAvoidingView
      behavior={Platform.OS === 'ios' ? 'padding' : 'height'}
      style={styles.container}
    >
      <Image source={icone} style={styles.logo} />
      <Text style={styles.title}>SpaceSync</Text>

      <View style={styles.form}>

        <TextInput
          style={styles.input}
          placeholder="Email"
          onChangeText={(text) => setEmail(text)}
          value={email}
          keyboardType="email-address"
        />
        <TextInput
          style={styles.input}
          placeholder="Senha"
          onChangeText={(text) => setSenha(text)}
          value={senha}
          secureTextEntry
        />

        <TouchableOpacity style={styles.buttonEntrar} >
          <Text style={styles.buttonCadastrarText} onPress={() => verLogin()}>ENTRAR</Text>
        </TouchableOpacity>
        <TouchableOpacity style={styles.buttonCadastrar} >
          <Text style={styles.buttonCadastrarText} onPress={() => navigation.navigate('cadastro')}>CADASTRE-SE</Text>
        </TouchableOpacity>


      </View>
    </KeyboardAvoidingView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
    paddingHorizontal: 20,
  },

  logo: {
    width: 250,
    height: 250,
    shadowColor: 'black',
    shadowOffset: { width: 0, height: 4 },
    shadowOpacity: 0.5,
    shadowRadius: 5,
  },

  title: {
    fontSize: 30,
    fontWeight: 'bold',
    marginBottom: 35,

  },

  form: {
    width: '100%',
    maxWidth: 300,
  },
  input: {
    marginBottom: 10,
    padding: 10,
    backgroundColor: '#C299DC',
    borderColor: '#ccc',
    borderRadius: 20,
    color: 'white'
  },
  buttonCadastrar: {
    width: '50%',
    height: '20%',
    borderRadius: 20,
    backgroundColor: '#924DC1',
    alignSelf: 'center',
    textAlign: 'center',

  },
  buttonCadastrarText: {
    paddingVertical: 10,
    color: 'white',
    fontWeight: 'BOLD',
  },
  buttonEntrar: {
    width: '50%',
    height: '20%',
    borderRadius: 20,
    backgroundColor: '#924DC1',
    alignSelf: 'center',
    textAlign: 'center',
    marginBottom: 10
  },

});
