import React, { useState, useEffect } from "react";
import { Text, View, StyleSheet, TextInput, TouchableOpacity, FlatList, KeyboardAvoidingView, Platform } from 'react-native';
import { AntDesign } from '@expo/vector-icons';
import { MaterialIcons } from '@expo/vector-icons';
import { MaterialCommunityIcons } from '@expo/vector-icons';

const Gerenciamento = () => {
  const [task, setTask] = useState([
    'Lab. 1', 'Lab. 2', 'Lab. 3', 'Lab. 4', 'Lab. 5', 'Lab. 6', 'Quadra',
    'Sala de informática', 'Sala temática', 'Lab. enfermagem', 'Lab. farmácia',
    'Lab. prancheta', 'Áudio visual', 'Oficina de artes', 'Lab. materiais',
    'Lab. prancheta 2', 'Lab. ciências e biologia'
  ]);

  const [newTask, setNewTask] = useState(""); // Input add tarefa

  const styles = StyleSheet.create({
    Body: {
      flex: 1,
    },
    botaoAdd: {
      alignItems: "center",
      height: 40,
      width: 40,
      justifyContent: "center",
      backgroundColor: '#924DC1',
      borderRadius: 10,
    },
    FlatList: {
      flex: 1,
      marginTop: 5,
    },
    containerView: {
      maxWidth: '80%',
      margin: 10, // Adjust this value as needed
      padding: 20,
      borderRadius: 10,
      backgroundColor: "#E1CBEE",
      display: "flex",
      flexDirection: "row",
      justifyContent: "space-between",
      borderWidth: 1,
      borderColor: '#eee',
    },
    iconsContainer: {
      flexDirection: "row",
      alignItems: "center",
    },
    icon: {
      marginHorizontal: 5,
    },
    Texto: {
      fontSize: 14,
      color: "#333",
      fontWeight: "bold",
      marginTop: 4,
      textAlign: "center",
      textTransform: 'uppercase',
    },
  });

  return (
    <KeyboardAvoidingView
      keyboardVerticalOffset={0}
      behavior="padding"
      style={{ flex: 1 }}
      enabled={Platform.OS === 'ios'}
    >
      <View style={styles.Body}>
        <FlatList
          style={styles.FlatList}
          data={task}
          keyExtractor={(item, index) => index.toString()}
          showsVerticalScrollIndicator={false}
          renderItem={({ item }) => (
            <View style={styles.containerView}>
              <Text style={styles.Texto}> {item} </Text>
              <View style={styles.iconsContainer}>
                <TouchableOpacity style={styles.icon}>
                  <MaterialIcons name="delete-forever" size={24} color="black" />
                </TouchableOpacity>
                <TouchableOpacity style={styles.icon}>
                  <MaterialCommunityIcons name="pencil" size={24} color="black" />
                </TouchableOpacity>
              </View>
            </View>
          )}
        />
      </View>
      <TouchableOpacity style={styles.botaoAdd}>
        <AntDesign name="plus" size={24} color="white" />
      </TouchableOpacity>
    </KeyboardAvoidingView>
  );
};

export default Gerenciamento;
